// Get requested date from query params
const requestedDate = $input.first().json.query?.date || new Date().toISOString().split('T')[0];

// Get existing appointments for this date
const appointments = $('Get Appointments').all();
const bookedSlots = appointments
  .filter(apt => apt.json.Date === requestedDate && 
                 ['pending', 'confirmed'].includes(apt.json.Status))
  .map(apt => apt.json.Start_Time);

// Work hours configuration
const workHours = {
  morningStart: 9,
  morningEnd: 12,
  afternoonStart: 13.5, // 13:30
  afternoonEnd: 17
};

// Generate all possible slots
const allSlots = [];

// Morning slots
for (let hour = workHours.morningStart; hour < workHours.morningEnd; hour++) {
  allSlots.push(`${hour.toString().padStart(2, '0')}:00`);
}

// Afternoon slots
allSlots.push('13:30');
for (let hour = 14; hour < workHours.afternoonEnd; hour++) {
  allSlots.push(`${hour.toString().padStart(2, '0')}:00`);
}

// Check if date is in the past
const today = new Date();
today.setHours(0, 0, 0, 0);
const reqDate = new Date(requestedDate);
reqDate.setHours(0, 0, 0, 0);

if (reqDate < today) {
  return [{ json: { success: true, slots: [], message: 'Date is in the past' } }];
}

// Check if weekend
const dayOfWeek = reqDate.getDay();
if (dayOfWeek === 0 || dayOfWeek === 6) {
  return [{ json: { success: true, slots: [], message: 'Weekend - clinic closed' } }];
}

// Filter out booked slots
const availableSlots = allSlots.map(time => ({
  time,
  available: !bookedSlots.includes(time)
}));

return [{
  json: {
    success: true,
    date: requestedDate,
    slots: availableSlots
  }
}];
