// Get date from query parameter
const queryDate = $('Webhook - Get Slots').first().json.query.date;

if (!queryDate) {
  return [{ json: { error: 'Date parameter is required', slots: [] } }];
}

// Parse the requested date
const requestedDate = new Date(queryDate);
const dayOfWeek = requestedDate.getDay();

// Check if it's a working day (Monday-Friday = 1-5)
const workingDays = [1, 2, 3, 4, 5];
if (!workingDays.includes(dayOfWeek)) {
  return [{ json: { date: queryDate, slots: [], message: 'Неработен ден' } }];
}

// Working hours configuration
const workingHours = {
  morning: { start: '09:00', end: '12:00' },
  afternoon: { start: '13:30', end: '17:00' }
};

const duration = 60; // Default appointment duration in minutes

// Helper function to convert time to minutes
function timeToMinutes(time) {
  const [hours, minutes] = time.split(':').map(Number);
  return hours * 60 + minutes;
}

// Helper function to convert minutes to time
function minutesToTime(mins) {
  const hours = Math.floor(mins / 60);
  const minutes = mins % 60;
  return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
}

// Generate all possible slots
const allSlots = [];

// Morning slots
let current = timeToMinutes(workingHours.morning.start);
const morningEnd = timeToMinutes(workingHours.morning.end);
while (current + duration <= morningEnd) {
  allSlots.push(minutesToTime(current));
  current += duration;
}

// Afternoon slots
current = timeToMinutes(workingHours.afternoon.start);
const afternoonEnd = timeToMinutes(workingHours.afternoon.end);
while (current + duration <= afternoonEnd) {
  allSlots.push(minutesToTime(current));
  current += duration;
}

// Get booked appointments for this date
const appointments = $('Get Appointments').all();
const bookedTimes = appointments
  .filter(apt => {
    const aptDate = apt.json.date;
    const status = apt.json.status;
    return aptDate === queryDate && status !== 'cancelled';
  })
  .map(apt => apt.json.startTime);

// Filter out booked slots
const availableSlots = allSlots.filter(slot => !bookedTimes.includes(slot));

return [{
  json: {
    date: queryDate,
    slots: availableSlots,
    totalSlots: allSlots.length,
    bookedSlots: bookedTimes.length
  }
}];
