parameters.jsCode:
// Get request body
const body = $('Webhook - Confirm').first().json.body;
const appointmentId = body.appointmentId;
const newStatus = body.status;
const newDuration = body.duration; // Optional - only for confirmation

if (!appointmentId || !newStatus) {
  return [{
    json: {
      success: false,
      error: 'appointmentId and status are required',
      found: false
    }
  }];
}

// Valid statuses
const validStatuses = ['pending', 'confirmed', 'completed', 'cancelled', 'expired'];
if (!validStatuses.includes(newStatus)) {
  return [{
    json: {
      success: false,
      error: `Invalid status. Must be one of: ${validStatuses.join(', ')}`,
      found: false
    }
  }];
}

// Find the appointment
const appointments = $('Read Appointments').all();
let appointment = null;

for (const apt of appointments) {
  if (apt.json.id === appointmentId) {
    appointment = apt.json;
    break;
  }
}

if (!appointment) {
  return [{
    json: {
      success: false,
      error: 'Appointment not found',
      found: false
    }
  }];
}

// Check for conflicts if confirming with a longer duration
let hasConflict = false;
let conflictWith = null;

if (newStatus === 'confirmed' && newDuration) {
  function timeToMinutes(time) {
    const [hours, minutes] = time.split(':').map(Number);
    return hours * 60 + minutes;
  }

  const aptStart = timeToMinutes(appointment.startTime);
  const aptEnd = aptStart + parseInt(newDuration);

  for (const other of appointments) {
    if (other.json.id === appointment.id) continue;
    if (other.json.date !== appointment.date) continue;
    if (other.json.status === 'cancelled' || other.json.status === 'expired') continue;

    const otherStart = timeToMinutes(other.json.startTime);
    const otherDuration = other.json.status === 'pending' ? 30 : (parseInt(other.json.duration) || 30);
    const otherEnd = otherStart + otherDuration;

    if (aptStart < otherEnd && aptEnd > otherStart) {
      hasConflict = true;
      conflictWith = { time: other.json.startTime, name: other.json.patientName };
      break;
    }
  }
}

if (hasConflict) {
  return [{
    json: {
      success: false,
      error: `–ö–æ–Ω—Ñ–ª–∏–∫—Ç —Å ${conflictWith.name} –≤ ${conflictWith.time}. –ò–∑–±–µ—Ä–µ—Ç–µ –ø–æ-–∫—Ä–∞—Ç–∫–∞ –ø—Ä–æ–¥—ä–ª–∂–∏—Ç–µ–ª–Ω–æ—Å—Ç.`,
      found: true,
      conflict: true
    }
  }];
}

// Prepare update data
const updateData = {
  id: appointmentId,
  status: newStatus,
  updatedAt: new Date().toISOString(),
  patientName: appointment.patientName,
  patientPhone: appointment.patientPhone,
  date: appointment.date,
  startTime: appointment.startTime,
  oldStatus: appointment.status,
  found: true,
  success: true
};

// Add duration if confirming
if (newStatus === 'confirmed' && newDuration) {
  updateData.duration = parseInt(newDuration);
}

return [{ json: updateData }];

