const query = $('Webhook').first().json.query || {};
let records = $('Read Finance').all().map(item => item.json);

// Filter by date
if (query.date) {
  records = records.filter(rec => rec.date === query.date);
}

// Filter by date range
if (query.startDate && query.endDate) {
  records = records.filter(rec => {
    return rec.date >= query.startDate && rec.date <= query.endDate;
  });
}

// Filter by type (income/expense)
if (query.type) {
  records = records.filter(rec => rec.type === query.type);
}

// Filter by category
if (query.category) {
  records = records.filter(rec => rec.category === query.category);
}

// Calculate totals (amounts are already +/- in EUR)
const totalIncome = records
  .filter(r => r.type === 'income')
  .reduce((sum, r) => sum + parseFloat(r.amount || 0), 0);

const totalExpense = records
  .filter(r => r.type === 'expense')
  .reduce((sum, r) => sum + Math.abs(parseFloat(r.amount || 0)), 0);

// Group by category
const byCategory = {};
records.forEach(rec => {
  const cat = rec.category || 'other';
  if (!byCategory[cat]) {
    byCategory[cat] = { income: 0, expense: 0, count: 0 };
  }
  byCategory[cat].count++;
  if (rec.type === 'income') {
    byCategory[cat].income += parseFloat(rec.amount || 0);
  } else {
    byCategory[cat].expense += Math.abs(parseFloat(rec.amount || 0));
  }
});

// Sort by date descending
records.sort((a, b) => (b.date || '').localeCompare(a.date || ''));

return [{
  json: {
    success: true,
    count: records.length,
    totalIncome: Math.round(totalIncome * 100) / 100,
    totalExpense: Math.round(totalExpense * 100) / 100,
    balance: Math.round((totalIncome - totalExpense) * 100) / 100,
    byCategory: byCategory,
    records: records,
    currency: 'EUR'
  }
}];
