// Get query parameters
const query = $('Webhook - Finance GET').first().json.query || {};
let records = $('Read Finance').all().map(item => item.json);

// Filter by date if provided
if (query.date) {
  records = records.filter(rec => rec.date === query.date);
}

// Filter by date range
if (query.startDate && query.endDate) {
  records = records.filter(rec => {
    return rec.date >= query.startDate && rec.date <= query.endDate;
  });
}

// Filter by type (official/custom)
if (query.type) {
  records = records.filter(rec => rec.type === query.type);
}

// Calculate totals
const totalIncome = records
  .filter(r => parseFloat(r.amount) >= 0)
  .reduce((sum, r) => sum + parseFloat(r.amount || 0), 0);

const totalExpense = records
  .filter(r => parseFloat(r.amount) < 0)
  .reduce((sum, r) => sum + Math.abs(parseFloat(r.amount || 0)), 0);

// Sort by date descending
records.sort((a, b) => (b.date || '').localeCompare(a.date || ''));

return [{
  json: {
    success: true,
    count: records.length,
    totalIncome: totalIncome,
    totalExpense: totalExpense,
    balance: totalIncome - totalExpense,
    records: records
  }
}];
