const callback = $input.first().json;
const callbackData = callback.callback_query.data;
const messageId = callback.callback_query.message.message_id;
const chatId = callback.callback_query.message.chat.id;

let action, duration, appointmentId;

if (callbackData.startsWith('confirm_')) {
  const parts = callbackData.split('_');
  action = 'confirm';
  duration = parseInt(parts[1]);
  appointmentId = parts.slice(2).join('_');
} else if (callbackData.startsWith('cancel_')) {
  action = 'cancel';
  duration = 0;
  appointmentId = callbackData.replace('cancel_', '');
} else {
  return [{ json: { error: 'Unknown action', valid: false } }];
}

return [{
  json: {
    action: action,
    duration: duration,
    appointmentId: appointmentId,
    messageId: messageId,
    chatId: chatId,
    valid: true
  }
}];

const parsed = $('Parse Callback').first().json;
const appointments = $('Get Appointments').all();

const apt = appointments.find(a => a.json.id === parsed.appointmentId);

if (!apt) {
  return [{ json: { error: 'Записът не е намерен', valid: false, action: 'error', chatId: parsed.chatId } }];
}

if (apt.json.status !== 'pending') {
  return [{ json: { error: 'Записът вече е обработен', valid: false, action: 'already_processed', chatId: parsed.chatId } }];
}

if (parsed.action === 'cancel') {
  return [{
    json: {
      id: apt.json.id,
      patientName: apt.json.patientName,
      patientPhone: apt.json.patientPhone,
      date: apt.json.date,
      startTime: apt.json.startTime,
      duration: apt.json.duration,
      reason: apt.json.reason || '',
      status: 'cancelled',
      createdAt: apt.json.createdAt,
      updatedAt: new Date().toISOString(),
      action: 'cancel',
      messageId: parsed.messageId,
      chatId: parsed.chatId,
      valid: true
    }
  }];
}

const newDuration = parsed.duration;

function timeToMinutes(time) {
  const [hours, minutes] = time.split(':').map(Number);
  return hours * 60 + minutes;
}

const aptStart = timeToMinutes(apt.json.startTime);
const aptEnd = aptStart + newDuration;

let hasConflict = false;
let conflictWith = null;

for (const other of appointments) {
  if (other.json.id === apt.json.id) continue;
  if (other.json.date !== apt.json.date) continue;
  if (other.json.status === 'cancelled' || other.json.status === 'expired') continue;

  const otherStart = timeToMinutes(other.json.startTime);
  const otherDuration = other.json.status === 'pending' ? 30 : (parseInt(other.json.duration) || 30);
  const otherEnd = otherStart + otherDuration;

  if (aptStart < otherEnd && aptEnd > otherStart) {
    hasConflict = true;
    conflictWith = { time: other.json.startTime, name: other.json.patientName };
    break;
  }
}

if (hasConflict) {
  return [{
    json: {
      action: 'conflict',
      conflictWith: conflictWith,
      requestedDuration: newDuration,
      patientName: apt.json.patientName,
      messageId: parsed.messageId,
      chatId: parsed.chatId,
      valid: false
    }
  }];
}

return [{
  json: {
    id: apt.json.id,
    patientName: apt.json.patientName,
    patientPhone: apt.json.patientPhone,
    date: apt.json.date,
    startTime: apt.json.startTime,
    duration: String(newDuration),
    reason: apt.json.reason || '',
    status: 'confirmed',
    createdAt: apt.json.createdAt,
    updatedAt: new Date().toISOString(),
    action: 'confirm',
    newDuration: newDuration,
    messageId: parsed.messageId,
    chatId: parsed.chatId,
    valid: true
  }
}];

// Prepare data for Google Sheets update (remove extra fields)
const data = $input.first().json;
return [{
  json: {
    id: data.id,
    patientName: data.patientName,
    patientPhone: data.patientPhone,
    date: data.date,
    startTime: data.startTime,
    duration: data.duration,
    reason: data.reason,
    status: data.status,
    createdAt: data.createdAt,
    updatedAt: data.updatedAt
  }
}];

// Prepare data for Google Sheets update (remove extra fields)
const data = $input.first().json;
return [{
  json: {
    id: data.id,
    patientName: data.patientName,
    patientPhone: data.patientPhone,
    date: data.date,
    startTime: data.startTime,
    duration: data.duration,
    reason: data.reason,
    status: data.status,
    createdAt: data.createdAt,
    updatedAt: data.updatedAt
  }
}];

