// Parse query parameters
const query = $input.first().json.query;

// Default to current week if no dates provided
const now = new Date();
let startDate, endDate;

if (query.startDate && query.endDate) {
  startDate = query.startDate;
  endDate = query.endDate;
} else if (query.view === 'day') {
  const date = query.date || now.toISOString().split('T')[0];
  startDate = date + 'T00:00:00+02:00';
  endDate = date + 'T23:59:59+02:00';
} else if (query.view === 'month') {
  const year = query.year || now.getFullYear();
  const month = query.month || now.getMonth();
  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  startDate = firstDay.toISOString();
  endDate = lastDay.toISOString();
} else {
  // Default: week view
  const day = now.getDay();
  const diff = now.getDate() - day + (day === 0 ? -6 : 1); // Monday
  const monday = new Date(now.setDate(diff));
  monday.setHours(0, 0, 0, 0);
  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);
  sunday.setHours(23, 59, 59, 999);
  startDate = monday.toISOString();
  endDate = sunday.toISOString();
}

return [{
  json: {
    startDate: startDate,
    endDate: endDate,
    view: query.view || 'week'
  }
}];

// Transform calendar events to our format
const events = $input.all();

// Map Google Calendar colorId (1-11) back to our color names
const colorIdToName = {
  '10': 'green',    // Basil
  '9': 'blue',      // Blueberry
  '11': 'red',      // Tomato
  '5': 'yellow',    // Banana
  '3': 'purple',    // Grape
  '6': 'orange',    // Tangerine
  '4': 'pink',      // Flamingo
  '8': 'gray'       // Graphite
};

const transformed = events.map(item => {
  const event = item.json;

  // Parse start/end times
  const startDateTime = event.start?.dateTime || event.start?.date;
  const endDateTime = event.end?.dateTime || event.end?.date;

  let startDate, startTime, endDate, endTime, isAllDay = false;

  if (event.start?.date) {
    // All-day event
    isAllDay = true;
    startDate = event.start.date;
    endDate = event.end.date;
    startTime = '00:00';
    endTime = '23:59';
  } else {
    // Timed event - extract time DIRECTLY from ISO string to preserve timezone
    // Format: 2026-01-29T09:30:00+02:00
    startDate = startDateTime.slice(0, 10); // "2026-01-29"
    endDate = endDateTime.slice(0, 10);
    startTime = startDateTime.slice(11, 16); // "09:30"
    endTime = endDateTime.slice(11, 16);
  }

  // Calculate duration in minutes
  const durationMs = new Date(endDateTime) - new Date(startDateTime);
  const duration = Math.round(durationMs / (1000 * 60));

  // Parse description for structured data
  const description = event.description || '';
  let phone = '';
  let procedure = '';

  // Try to extract phone from description
  const phoneMatch = description.match(/(?:Ñ‚ÐµÐ»|phone|ðŸ“ž)[:\s]*([+\d\s-]+)/i);
  if (phoneMatch) phone = phoneMatch[1].trim();

  // Try to extract procedure
  const procMatch = description.match(/(?:Ð¿Ñ€Ð¾Ñ†ÐµÐ´ÑƒÑ€Ð°|procedure|ðŸ¦·)[:\s]*(.+?)(?:\n|$)/i);
  if (procMatch) procedure = procMatch[1].trim();

  // Map Google colorId to our color name - DEFAULT IS GREEN
  const colorId = colorIdToName[event.colorId] || 'green';

  return {
    json: {
      id: event.id,
      googleEventId: event.id,
      title: event.summary || 'Ð‘ÐµÐ· Ð·Ð°Ð³Ð»Ð°Ð²Ð¸Ðµ',
      patientName: event.summary || '',
      patientPhone: phone,
      procedure: procedure,
      description: description,
      date: startDate,
      startTime: startTime,
      endTime: endTime,
      endDate: endDate,
      duration: duration,
      isAllDay: isAllDay,
      colorId: colorId,
      status: event.status,
      location: event.location || '',
      htmlLink: event.htmlLink
    }
  };
});

return transformed;
