// Parse and validate input
const body = $input.first().json.body;

// Required: event ID
if (!body.eventId && !body.id && !body.googleEventId) {
  throw new Error('ID –Ω–∞ —Å—ä–±–∏—Ç–∏–µ—Ç–æ –µ –∑–∞–¥—ä–ª–∂–∏—Ç–µ–ª–Ω–æ');
}

const eventId = body.eventId || body.id || body.googleEventId;

// Map color names to Google Calendar colorId (1-11)
const colorMap = {
  'green': '10',    // Basil (green)
  'blue': '9',      // Blueberry (blue)
  'red': '11',      // Tomato (red)
  'yellow': '5',    // Banana (yellow)
  'purple': '3',    // Grape (purple)
  'orange': '6',    // Tangerine (orange)
  'pink': '4',      // Flamingo (pink)
  'gray': '8'       // Graphite (gray)
};

// Build update fields
const updateFields = {};

// If moving to new date/time
if (body.date && body.startTime) {
  const duration = body.duration || 30;
  const [hours, minutes] = body.startTime.split(':').map(Number);
  const startDate = new Date(`${body.date}T${body.startTime}:00`);
  const endDate = new Date(startDate.getTime() + duration * 60 * 1000);
  const endTime = body.endTime || `${String(endDate.getHours()).padStart(2, '0')}:${String(endDate.getMinutes()).padStart(2, '0')}`;

  updateFields.startDateTime = `${body.date}T${body.startTime}:00`;
  updateFields.endDateTime = `${body.date}T${endTime}:00`;
}

// Update title/summary
if (body.patientName) {
  updateFields.summary = body.patientName;
}

// Build description
let description = '';
if (body.patientPhone !== undefined || body.procedure !== undefined || body.notes !== undefined) {
  if (body.patientPhone) {
    description += `üìû –¢–µ–ª: ${body.patientPhone}\n`;
  }
  if (body.procedure) {
    description += `ü¶∑ –ü—Ä–æ—Ü–µ–¥—É—Ä–∞: ${body.procedure}\n`;
  }
  if (body.notes) {
    description += `üìù –ë–µ–ª–µ–∂–∫–∏: ${body.notes}\n`;
  }
  updateFields.description = description.trim();
}

// Handle colorId
if (body.colorId && colorMap[body.colorId]) {
  updateFields.colorId = colorMap[body.colorId];
}

return [{
  json: {
    eventId: eventId,
    updateFields: updateFields,
    originalData: body
  }
}];

// Get updated event details
const event = $input.first().json;
const prevData = $('Prepare Update Data').first().json.originalData;

// Parse times
const startDateTime = event.start?.dateTime || event.start?.date;
const endDateTime = event.end?.dateTime || event.end?.date;
let startDate, startTime, endTime;

if (startDateTime) {
  const start = new Date(startDateTime);
  const end = new Date(endDateTime);
  startDate = start.toISOString().split('T')[0];
  startTime = start.toTimeString().slice(0, 5);
  endTime = end.toTimeString().slice(0, 5);
}

return [{
  json: {
    id: event.id,
    googleEventId: event.id,
    title: event.summary,
    date: startDate,
    startTime: startTime,
    endTime: endTime,
    description: event.description,
    status: event.status,
    htmlLink: event.htmlLink,
    updated: event.updated
  }
}];
