// Validate incoming booking data
const body = $input.first().json.body;

const required = ['patientName', 'patientPhone', 'date', 'startTime'];
const missing = required.filter(field => !body[field]);

if (missing.length > 0) {
  return [{
    json: {
      valid: false,
      error: `–õ–∏–ø—Å–≤–∞—â–∏ –ø–æ–ª–µ—Ç–∞: ${missing.join(', ')}`
    }
  }];
}

// Validate phone number (Bulgarian format)
const phone = body.patientPhone.replace(/\s/g, '');
const phoneRegex = /^(\+359|0)[0-9]{9}$/;
if (!phoneRegex.test(phone)) {
  return [{
    json: {
      valid: false,
      error: '–ù–µ–≤–∞–ª–∏–¥–µ–Ω —Ç–µ–ª–µ—Ñ–æ–Ω–µ–Ω –Ω–æ–º–µ—Ä. –ú–æ–ª—è, –∏–∑–ø–æ–ª–∑–≤–∞–π—Ç–µ —Ñ–æ—Ä–º–∞—Ç: 0888123456 –∏–ª–∏ +359888123456'
    }
  }];
}

// Validate date is in the future
const bookingDate = new Date(body.date + 'T' + body.startTime);
const now = new Date();
if (bookingDate < now) {
  return [{
    json: {
      valid: false,
      error: '–ù–µ –º–æ–∂–µ –¥–∞ –∑–∞–ø–∞–∑–∏—Ç–µ —á–∞—Å –≤ –º–∏–Ω–∞–ª–æ—Ç–æ'
    }
  }];
}

// Check if it's a working day
const dayOfWeek = new Date(body.date).getDay();
if (dayOfWeek === 0 || dayOfWeek === 6) {
  return [{
    json: {
      valid: false,
      error: '–°—ä–±–æ—Ç–∞ –∏ –Ω–µ–¥–µ–ª—è —Å–∞ –ø–æ—á–∏–≤–Ω–∏ –¥–Ω–∏'
    }
  }];
}

// Default duration is 30 minutes for new bookings (doctor will confirm actual duration)
const duration = 30;

return [{
  json: {
    valid: true,
    patientName: body.patientName.trim(),
    patientPhone: phone,
    date: body.date,
    startTime: body.startTime,
    duration: duration,
    reason: (body.reason || '').trim(),
    notes: (body.notes || '').trim()
  }
}];

// Get the booking request
const booking = $('Validate Input').first().json;
const bookingDate = booking.date;
const bookingTime = booking.startTime;
const bookingDuration = booking.duration;

// Helper function
function timeToMinutes(time) {
  const [hours, minutes] = time.split(':').map(Number);
  return hours * 60 + minutes;
}

const bookingStart = timeToMinutes(bookingTime);
const bookingEnd = bookingStart + bookingDuration;

// Get existing calendar events
const calendarEvents = $input.all();

// Check for conflicts
let hasConflict = false;
let conflictWith = null;

for (const item of calendarEvents) {
  const event = item.json;

  // Skip cancelled events
  if (event.status === 'cancelled') continue;

  let existingStart, existingEnd;

  if (event.start?.dateTime) {
    const start = new Date(event.start.dateTime);
    const end = new Date(event.end.dateTime);
    existingStart = start.getHours() * 60 + start.getMinutes();
    existingEnd = end.getHours() * 60 + end.getMinutes();
  } else if (event.start?.date) {
    // All-day event blocks entire day
    hasConflict = true;
    conflictWith = '—Ü–µ–ª–æ–¥–Ω–µ–≤–Ω–æ —Å—ä–±–∏—Ç–∏–µ';
    break;
  } else {
    continue;
  }

  // Check overlap: newStart < existingEnd AND newEnd > existingStart
  if (bookingStart < existingEnd && bookingEnd > existingStart) {
    hasConflict = true;
    const startHour = Math.floor(existingStart / 60);
    const startMin = existingStart % 60;
    conflictWith = `${startHour.toString().padStart(2, '0')}:${startMin.toString().padStart(2, '0')}`;
    break;
  }
}

if (hasConflict) {
  return [{
    json: {
      conflict: true,
      error: `–¢–æ–∑–∏ —á–∞—Å –µ –≤–µ—á–µ –∑–∞–µ—Ç (–∫–æ–Ω—Ñ–ª–∏–∫—Ç —Å ${conflictWith})`
    }
  }];
}

// No conflict - prepare data for calendar event creation
const id = 'apt_' + Date.now().toString(36) + Math.random().toString(36).substr(2, 8);

return [{
  json: {
    conflict: false,
    id: id,
    patientName: booking.patientName,
    patientPhone: booking.patientPhone,
    date: booking.date,
    startTime: booking.startTime,
    duration: booking.duration,
    reason: booking.reason,
    notes: booking.notes
  }
}];

// Prepare calendar event data
const booking = $json;

// Calculate end time
const [hours, minutes] = booking.startTime.split(':').map(Number);
const startMinutes = hours * 60 + minutes;
const endMinutes = startMinutes + booking.duration;
const endHours = Math.floor(endMinutes / 60);
const endMins = endMinutes % 60;
const endTime = `${endHours.toString().padStart(2, '0')}:${endMins.toString().padStart(2, '0')}`;

// Build description with structured data
let description = `üìû –¢–µ–ª: ${booking.patientPhone}\n`;
if (booking.reason) {
  description += `üìã –ü—Ä–∏—á–∏–Ω–∞: ${booking.reason}\n`;
}
if (booking.notes) {
  description += `üìù –ë–µ–ª–µ–∂–∫–∏: ${booking.notes}\n`;
}
description += `\n‚è≥ –°—Ç–∞—Ç—É—Å: –ß–ê–ö–ê–© (pending)\n`;
description += `üÜî ID: ${booking.id}\n`;
description += `üìÖ –°—ä–∑–¥–∞–¥–µ–Ω: ${new Date().toLocaleString('bg-BG')}`;

return [{
  json: {
    summary: `‚è≥ ${booking.patientName}`,
    description: description,
    startDateTime: `${booking.date}T${booking.startTime}:00`,
    endDateTime: `${booking.date}T${endTime}:00`,
    colorId: '5',  // Yellow for pending
    id: booking.id,
    patientName: booking.patientName,
    patientPhone: booking.patientPhone
  }
}];

// Prepare success response
const calendarEvent = $input.first().json;
const preparedData = $('Prepare Event Data').first().json;

return [{
  json: {
    success: true,
    message: '–ß–∞—Å—ä—Ç –µ –∑–∞–ø–∞–∑–µ–Ω —É—Å–ø–µ—à–Ω–æ! –û—á–∞–∫–≤–∞–π—Ç–µ –ø–æ—Ç–≤—ä—Ä–∂–¥–µ–Ω–∏–µ.',
    booking: {
      id: preparedData.id,
      googleEventId: calendarEvent.id,
      patientName: preparedData.patientName,
      patientPhone: preparedData.patientPhone,
      date: preparedData.startDateTime.split('T')[0],
      startTime: preparedData.startDateTime.split('T')[1].slice(0, 5),
      status: 'pending'
    }
  }
}];

